
public class Swim {
	boolean waterisWarm;
	public Swim(boolean w) {
		// TODO Auto-generated constructor stub
		this.waterisWarm = w;
		
		if(waterisWarm){
			System.out.println("The water is warm, go for a swim!");
		}else{
			System.out.println("The water is cold, get out of the pool!");
		}
		
		
	}

}
