
public class P10 {
	//private variables
	private String name;
	private String type;
	private int age;
	//constructor
	public P10() {
		// TODO Auto-generated constructor stub
	}
	//getters and setter
	public void setName(String name){
		this.name = name;
	}
	public void setType(String type){
		this.type = type;
	}
	public void setAge(int age){
		this.age = age;
	}
	
	public String getName(){
		return name;
	}
	public String getType(){
		return type;
	}

	public int getAge(){
		return age;
	}


}
